import React, { useEffect, useState } from "react";
import { View, FlatList, Text, Button, StyleSheet } from "react-native";
import { api } from "../api/api";

export default function ConsultantsScreen({ navigation, route }) {
  const [list, setList] = useState([]);
  const userId = route.params.userId;

  useEffect(() => {
    (async () => {
      const res = await api.get("/consultants");
      setList(res.data);
    })();
  }, []);

  return (
    <View style={{ flex: 1, padding: 16 }}>
      <FlatList
        data={list}
        keyExtractor={(item) => item._id}
        renderItem={({ item }) => (
          <View style={styles.card}>
            <Text style={styles.name}>{item.user.name}</Text>
            <Text style={styles.bio}>{item.bio}</Text>

            <Button
              title="Book Appointment"
              color="#3b82f6"
              onPress={() =>
                navigation.navigate("Booking", { consultant: item, userId })
              }
            />
          </View>
        )}
      />
    </View>
  );
}

const styles = StyleSheet.create({
  card: {
    backgroundColor: "white",
    padding: 16,
    borderRadius: 12,
    marginBottom: 12,
    elevation: 3
  },
  name: { fontSize: 20, fontWeight: "bold", color: "#111" },
  bio: { marginVertical: 8, color: "#555" }
});
